import axiosIntance from "../helpers/axios"

export const  login = (user)=>{
    return async (dispatch) =>{
        const res = await axiosIntance.post('/')
        dispatch({type:"LOGIN_REQUEST",payload:{
            ...user
        }})
    }
}
