import {fetchLogin} from "../helpers/axios"
export const LOGIN_REQUEST = 'LOGIN_REQUEST'


export const login =(user)=> async (dispatch) =>{
    try{
        dispatch ({
            type:LOGIN_REQUEST,
            payload:{... user}
        })
        const res =await fetchLogin (user);
        if (res.status === 200){
            const {token,user} = res.data
            localStorage.setItem('token',token)
            dispatch({
                type:'LOGIN_SUCCESS',
                payload:{
                    token,user
                }
            })
        }

        console.log("payload")
    }
    catch (error) {
              console.log(error);
             }
}
