import {fetchLogin} from "../helpers/axios"
export const LOGIN_REQUEST = 'LOGIN_REQUEST'
export const  login = (user)=>{
    return dispatch => {
        fetchLogin()
        .then (user =>dispatch (
            {type: LOGIN_REQUEST,payload: {
                ...user
            }}) 
        )

        .catch(err=>dispatch( {
            type: "GET_PLATS_FAILED",
            payload: err
          }))
    }

}
