import LOGIN_REQUEST from '../'

const inisialState ={
    name:{}
}

 const authReducer =  (state =inisialState,action)=>{
 switch (action.type){
     case "LOGIN_REQUEST":
         state={
             ...state,
             ...action.payload
         }
         break
 }
 return state
};
export default authReducer
