import {fetchLogin} from "../helpers/axios"
export const LOGIN_REQUEST = 'LOGIN_REQUEST'
export const  login = (user)=>{
    return async (dispatch) =>{
         dispatch({type:LOGIN_REQUEST,payload:{
            ...user
        }})
    }
}

export function getAllPlats() {
    return dispatch => {
        fetchLogin()
        .then (plats =>dispatch (
            {type: LOGIN_REQUEST,payload: plats}) 
        )

        .catch(err=>dispatch( {
            type: "GET_PLATS_FAILED",
            payload: err
          }))
    }

}
